// --- Mention User ---
const userId = "664803448629493816";
const mention = `<@${userId}>`;

// --- Ambil output dari node sebelumnya (opsional) ---
const stdout = $input.first()?.json?.stdout || "‚ö† Tidak ada output dari script.";

// --- Ambil waktu sekarang ---
const now = new Date();

// --- Hari dalam bahasa Inggris (Monday, Tuesday, ...) ---
const dayNames = ["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"];
const rawDay = dayNames[now.getDay()];

// --- Jam sekarang dalam HH:mm ---
const pad = (n) => String(n).padStart(2, "0");
const rawTime = `${pad(now.getHours())}:${pad(now.getMinutes())}`;

// --- Normalisasi nama hari ---
const dayMap = {
  'senin': 'Monday', 'selasa': 'Tuesday', 'rabu': 'Wednesday', 'kamis': 'Thursday', 'jumat': 'Friday', 'jum\'at': 'Friday',
  'mon': 'Monday', 'monday': 'Monday',
  'tue': 'Tuesday', 'tues': 'Tuesday', 'tuesday': 'Tuesday',
  'wed': 'Wednesday', 'wednesday': 'Wednesday',
  'thu': 'Thursday', 'thur': 'Thursday', 'thursday': 'Thursday',
  'fri': 'Friday', 'friday': 'Friday'
};
const dayKey = dayMap[rawDay.toLowerCase()] || rawDay;

// --- Jadwal kuliah ---
const schedule = {
  Monday: [
    { time: "08:00", end: "09:40", subject: "Metodologi Penelitian", lecturer: "Dr. Maulisa Oktiana", room: "Lab Elektro IV" }
  ],
  Tuesday: [
    { time: "10:45", end: "12:25", subject: "Artificial Inteligent", lecturer: "Ir. Yudha Nurdin, ST., MT", room: "A14-302" }
  ],
  Wednesday: [
    { time: "10:40", end: "12:25", subject: "Sistem Terdistribusi", lecturer: "Ir. Yudha Nurdin, ST., MT", room: "A25-201" },
    { time: "14:00", end: "15:40", subject: "Pengolahan Sinyal Multimedia", lecturer: "Dr. Maulisa Oktiana", room: "A12-302" }
  ],
  Thursday: [
    { time: "08:00", end: "09:40", subject: "Keamanan Informasi", lecturer: "Ir. Sayed Muchalili, S.T., M.Sc., IPM", room: "A12-304" },
    { time: "10:45", end: "13:15", subject: "Keamanan Jaringan", lecturer: "Prof. Dr. Ir. Teuku Yuliar Arif, S.T., M.Kom", room: "A24-102" },
    { time: "14:00", end: "15:45", subject: "Sistem Embedded", lecturer: "Ir. Rahmad Dawood, S.Kom., M.Sc., IPM", room: "Lab Terpadu" }
  ],
  Friday: [
    { time: "08:00", end: "09:40", subject: "Praktikum Distribusi", lecturer: "Pak Yudha", room: "A12-304" },
    { time: "16:30", end: "18:15", subject: "Tata Tulis Ilmiah", lecturer: "Dr. Maulisa Oktiana", room: "A12-304" }
  ]
};

// --- Helper: parse HH:MM ke menit ---
function parseTimeToMinutes(t) {
  if (!t) return null;
  t = String(t).trim().replace(/\./g, ':');
  const m = t.match(/(\d{1,2}):(\d{2})/);
  if (!m) return null;
  let hh = parseInt(m[1], 10);
  let mm = parseInt(m[2], 10);
  if (isNaN(hh) || isNaN(mm)) return null;
  return hh * 60 + mm;
}

let mataKuliah = "‚ùì Tidak ada jadwal terdaftar.";
let dosen = "-";
let ruangan = "-";
let jadwalWaktu = "-";

const nowMinutes = parseTimeToMinutes(rawTime);

// --- Cari jadwal sesuai hari & waktu ---
if (dayKey && schedule[dayKey]) {
  const rows = schedule[dayKey];

  if (nowMinutes !== null) {
    // cek kelas yang sedang berlangsung
    for (const r of rows) {
      const start = parseTimeToMinutes(r.time);
      const end = parseTimeToMinutes(r.end);
      if (start !== null && end !== null && nowMinutes >= start && nowMinutes <= end) {
        mataKuliah = r.subject;
        dosen = r.lecturer;
        ruangan = r.room;
        jadwalWaktu = `${r.time} - ${r.end}`;
        break;
      }
    }
  }

  // kalau belum ada kelas sekarang, ambil yang berikutnya
  if (mataKuliah.startsWith("‚ùì") && nowMinutes !== null) {
    const upcoming = rows
      .map(r => ({ ...r, start: parseTimeToMinutes(r.time) }))
      .filter(r => r.start !== null && r.start > nowMinutes)
      .sort((a, b) => a.start - b.start);

    if (upcoming.length > 0) {
      const next = upcoming[0];
      mataKuliah = `Tidak ada kelas sekarang. Berikutnya: ${next.subject}`;
      dosen = next.lecturer;
      ruangan = next.room;
      jadwalWaktu = `${next.time} - ${next.end}`;
    }
  }
} else {
  mataKuliah = "‚ùå Hari ini tidak ada jadwal kuliah.";
}

// --- Format pesan final ---
const finalMessage =
  `üïú **Absen Otomatis**
${mention}

üìå Status: ${stdout}
üìÖ Hari: ${rawDay}
‚è∞ Jam: ${rawTime}
üìñ Mata Kuliah: ${mataKuliah}
üë®‚Äçüè´ Dosen: ${dosen}
üè´ Ruangan: ${ruangan}
üóì Jadwal: ${jadwalWaktu}`;

return [{ json: { text: finalMessage } }];
