const output = $('update system').first().json.stdout
let message = "";
let status = "‚úÖ";
const logSnippet = output.slice(-1000);
const userId = "664803448629493816"; // Ganti dengan user Discord yang ingin ditag
const mention = `<@${userId}>`;

if (output.toLowerCase().includes("error") || output.toLowerCase().includes("failed")) {
  status = "‚ùå";
  message = `\`\`\`\n${logSnippet}\n\`\`\`\n‚ùå Update failed!\n${mention}`;
} else if (output.toLowerCase().includes("a reboot is required")) {
  status = "üîÑ";
  message = `\`\`\`\n${logSnippet}\n\`\`\`\nüîÑ Update completed but reboot is required.\n${mention}`;
} else {
  status = "‚úÖ";
  message = `\`\`\`\n${logSnippet}\n\`\`\`\n‚úÖ System updated successfully!`;
