const rawLog = $input.first().json.stdout;  // log input dari node sebelumnya

const updatedImages = [];
const failedImages = [];

const lines = rawLog.split("\n");

for (let i = 0; i < lines.length; i++) {
  const line = lines[i].trim();

  // Cek baris "Pulling image"
  if (line.startsWith("[*] Pulling image:")) {
    const image = line.replace("[*] Pulling image: ", "").trim();
    const statusLine = lines[i + 3] || "";  // Baris ke-3 setelah ini biasanya status
    const errorLine = lines[i + 1] || "";   // Jika error, muncul setelah ini

    if (statusLine.includes("Image is up to date")) {
      // Lewatkan (tidak perlu dicatat)
    } else if (errorLine.includes("Error response from daemon")) {
      failedImages.push(`${image} ‚ùå`);
    } else {
      updatedImages.push(`${image} ‚úÖ`);
    }
  }
}

// Format pesan
const startedAt = rawLog.match(/Started at (.+?) ===/i)?.[1] || "Unknown";
const completedAt = rawLog.match(/Completed at (.+?) ===/i)?.[1] || "Unknown";

let message = `üõ†Ô∏è **Docker Auto Update Summary**\n\n`;
message += `üïì **Started**: ${startedAt}\n‚úÖ **Completed**: ${completedAt}\n\n`;

if (updatedImages.length > 0) {
  message += `üîÑ **Updated Images:**\n${updatedImages.map(i => `‚Ä¢ ${i}`).join("\n")}\n\n`;
}

if (failedImages.length > 0) {
  message += `‚ùó **Failed Pulls:**\n${failedImages.map(i => `‚Ä¢ ${i}`).join("\n")}\n\n`;
}

if (updatedImages.length === 0 && failedImages.length === 0) {
  message += `üì¶ All containers already up to date.\n`;
}

return [
  {
    json: {
      content: message  // Output ke Discord node
    }
  }
];
